    [~, ~, excelRaw]=xlsread(excelFile,1, ['A',num2str(excelRow),':V',num2str(excelRow)]);
    recDate = excelRaw{1}; recDate = string(recDate);
    mouseName = excelRaw{2}; mouseName = string(mouseName);
    rawdataloc = excelRaw{3};
    saveDir = excelRaw{4}; saveDir = fullfile(string(saveDir),recDate);
    sessionType = excelRaw{6}; sessionType = sessionType(3:end-2);
    if ~exist(saveDir)
        mkdir(saveDir)
    end
    saveDir_new = strcat('L:\RGECO\Kenny\', recDate, '\');
    maskName = strcat(recDate,'-',mouseName,'-',sessionType,'1-datafluor','.mat');
    if ~exist(fullfile(saveDir_new,maskName),'file')
        maskName = strcat(recDate,'-',mouseName,'-LandmarksAndMask','.mat');
        load(fullfile(saveDir,maskName),'xform_isbrain')
    else
        load(fullfile(saveDir_new,maskName),'xform_isbrain')
    end
    AtlasMask = AtlasSeeds>0;
    mask = xform_isbrain.*AtlasMask;
    for n = runs
        visName = strcat(recDate,'-',mouseName,'-',sessionType,num2str(n));
        processedName = strcat(recDate,'-',mouseName,'-',sessionType,num2str(n),'_processed','.mat');
        load(fullfile(saveDir,processedName),'xform_datahb','xform_jrgeco1aCorr')
        HbT = squeeze(xform_datahb(:,:,1,:)+xform_datahb(:,:,2,:))*10^6;% convert to muM
        clear xform_datahb 
        Calcium = squeeze(xform_jrgeco1aCorr)*100; % convert to DeltaF/F%
        clear xform_jrgeco1aCorr

        %1.) Filter 0.01-5Hz, downsample to 10 Hz
        HbT_regions =  filterData(HbT_regions,0.01,5,samplingRate);
        Calcium_regions = filterData(Calcium_regions,0.01,5,samplingRate);

        HbT_regions = resample(HbT_regions,10,samplingRate,'Dimension',2); %resample to 10 Hz
        Calcium_regions = resample(Calcium_regions,10,samplingRate,'Dimension',2); %resample to 10 Hz
        %reshape into 30 seconds
        HbT_regions=reshape(HbT_regions,40,30*10,[]);
        Calcium_regions=reshape(Calcium_regions,40,30*10,[]); 
        
                %1.) Create regional average signals of HbT and Calcium (Step1)
        HbT_regions = nan(numReg,size(HbT,3));
        Calcium_regions = nan(numReg,size(Calcium,3));
        HbT = reshape(HbT,128*128,[]);
        Calcium = reshape(Calcium,128*128,[]);
        AtlasSeeds = AtlasSeeds.*xform_isbrain;
        for reg = 1:numReg
            ROI = AtlasSeeds == reg;
            HbT_regions(reg,:) = mean(HbT(ROI(:),:));
            Calcium_regions(reg,:) = mean(Calcium(ROI(:),:));
        end

        
        %.3) Then FOR EACH REGION (Left and Right, i.e., regHbT and regCalcium)
        h = nan(numReg,size(HbT_regions,3),size(HbT_regions,2));      
        for reg=1:numReg
            for win=1:(size(HbT_regions,2)*size(HbT_regions,3))/(30*10)
            regHbT = tukeywin(length(squeeze(HbT_regions(reg,:,win))),.3).*(squeeze(HbT_regions(reg,:,win)))'; %make sure these are column vectors!
            regCalcium = tukeywin(length(squeeze(Calcium_regions(reg,:,win))),.3).*(squeeze(Calcium_regions(reg,:,win)))'; %make sure these are column vectors!
            X = convmtx(regCalcium,length(regCalcium));% why calculating convolution matrix for input? 599*300?
            X = X(1:length(regCalcium),1:length(regCalcium));% make it square?
            [~,S,~]=svd(X);
            h(reg,win,:)= (X'*S*X+(S(1,1).^2)*.5*eye(length(regCalcium))) \ (X'*S*[zeros(3*10,1); regHbT(1:end-3*10)]);% why add 3s of zeros? Do we need to shift it?
            end
        end
        h_regions = squeeze(mean(h,2));
        saveName = strcat(recDate,'-',mouseName,'-',sessionType,num2str(n),'_NVC','.mat');
        save(fullfile(saveDir,saveName),'h','h_regions')
    end
end



